			_setFFProperty(oController);
			_setFFLabel(oController);
			_setFFAlias(oController);
			_setFFSelectionMode(oController);
			_setDefaultValueMode(oController);
			_setValueHelpMode(oController);
			_setUseVHRCheckBox(oController);
