		oObject = this.getProperty("lrepObject");
		
		if (oObject) {		
			if (oObject.name) {
				oResult.name = oObject.name;
			}
			
			if (oObject.type) {
				oResult.type = oObject.type;
			}
			
			if (oObject.namespace) {
				oResult.namespace = oObject.namespace;
			}
		}
		
